from urllib.parse import urlsplit, parse_qsl, unwrapdef parse(url: str) -> dict:	result = parse_qsl(urlsplit(url).query)	new_dict = {}	for item in result:		new_dict[item[0]] = item[1]	return dict(result)if __name__ == '__main__':	assert parse('https://example.com/path/to/page?name=ferret&color=purple') == {'name': 'ferret', 'color': 'purple'}	assert parse('https://example.com/path/to/page?name=ferret&color=purple&') == {'name': 'ferret', 'color': 'purple'}	assert parse('http://example.com/') == {}	assert parse('http://example.com/?') == {}	assert parse('http://example.com/?name=Dima') == {'name': 'Dima'}	assert parse('http://example.com/users/admin?name=Dima') == {'name': 'Dima'}	assert parse('http://example.com/users/admin/index.php') == {}	assert parse('http://example.com/cats?name=shinshilla&color=silver') == {'name': 'shinshilla', 'color': 'silver'}	assert parse('http://example.com/admin/new-cat?breed=british&color=white') == {'breed': 'british', 'color': 'white'}	assert parse('http://example.com/blog/about/cats?breed=scottish&color=all') == {'breed': 'scottish', 'color': 'all'}